import React from "react";
import Card from "./Card/Card";
import Full from "./Full/Full"
import cards from '../../json/housing.json'
import './Logement.css'

function LogementComponentFactory(route) {
    switch (route) {
        case route = '/':
            return  <div className='cardList'>{cards.map(card => 
                        <Card key={card.id} id={card.id} title={card.title} description={card.description} cover={card.cover} />)}
                    </div>;
        case route = '/housing/:id':
            return  <Full />;
        default:
            return  <p>Erreur</p>
    }
}

export default function Logement(props) {
    const component = LogementComponentFactory(props.route);
    return (
        <div>
            {component}
        </div>
    );
}

