import React from "react";
import Card from "./Card/Card";
import Full from "./Full/Full"
import cards from '../../json/housing.json'
import './Logement.css'

import { useContext } from 'react';
import { RouterContext } from 'react-router-dom';

export default function Logement(){
    const {location} = useContext(RouterContext);
    const cardList = cards.map(card => (
        <Card key={card.id} id={card.id} title={card.title} description={card.description} cover={card.cover} />
    ))

    return (
        <div>
            { location.pathname === '/'
                ? <div className='cardList'>{cardList}</div>
                : <Full />
            }
        </div>
    )
}

