import React from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import Card from "./Card/Card";
import Full from "./Full/Full"
import './Logement.css'

class Logements {
    constructor(label) {
      this.label = label;
    }

    render() {
      return <div className='Logements'>{this.label}</div>;
    }
  }

  class LogementsFactory {
    static createLogement(label) {
      return new Logements(label);
    }
  }

  export default function Logement() {
    const CardLogement = LogementsFactory.createLogement(<Card />);
    const FullLogement = LogementsFactory.createLogement(<Full />);

    return (
        <Router>
            <div>
                <Route path="/" exact component={() => CardLogement.render()} />
                <Route path="/housing/:id" component={() => FullLogement.render()} />
            </div>
        </Router>
    );
}

