import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import Card from "./Card/Card";
import Full from "./Full/Full";
import Error from './../../pages/Error/'
import cards from '../../json/housing.json'
import './Logement.css'

export default function Logement(){
    const location = useLocation();
    const [selectedCard, setSelectedCard] = useState(null);

    const handleClick = (card) => {
        setSelectedCard(card);
    }

    const cardElements = cards.map(card => (
        <Card key={card.id} id={card.id} title={card.title} description={card.description} cover={card.cover} handleClick={handleClick} />
    ));

    useEffect(() => {
        const id = location.pathname.split("/").pop();
        const selected = cards.find(c => c.id === id);
        setSelectedCard(selected);
    }, [location]);

    return (
        <div>
            {location.pathname === '/' ? 
                <div className='cardList'>
                    {cardElements}
                </div>
            : <Full card={selectedCard} /> 
            }
        </div>
    );
}
