import React from 'react';
import './Home.css';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import bannière from '../../assets/IMG.png'
import Card from '../../components/Card/Card';
import Banner from '../../components/Banner/Banner'

function Home() {

  const cards = [    
    { id: 1, title: 'Card 1', content: 'Card 1 content' },
    { id: 2, title: 'Card 2', content: 'Card 2 content' },
    { id: 3, title: 'Card 3', content: 'Card 3 content' },
    { id: 4, title: 'Card 4', content: 'Card 4 content' },
    { id: 5, title: 'Card 5', content: 'Card 5 content' },
    { id: 6, title: 'Card 6', content: 'Card 6 content' },
  ]

  const cardList = cards.map(card => (
    <Card key={card.id} title={card.title} content={card.content}  />
  ))

  return (
    <div className="App">
      <Header />
      <section className='body'>
        <div className='bannière'>
          <Banner src={bannière} alt='bannière'/>
          <h1 className='bannièreText'>Chez vous, partout et ailleurs</h1>
        </div>
        <div className='housing'>
        {cardList}
        </div>
      </section>
      <Footer />
    </div>
  );
}

export default Home;
