import React from "react";
import './Carouselle.css'
import { useState } from "react";

export default function Carouselle(props){
    const [currentIndex, setCurrentIndex] = useState(0);

    const previousSlide = () => {
      const newIndex = currentIndex - 1;
      setCurrentIndex(newIndex < 0 ? props.images.length - 1 : newIndex);
    };

    const nextSlide = () => {
      const newIndex = currentIndex + 1;
      setCurrentIndex(newIndex === props.images.length ? 0 : newIndex);
    };

    return (
      <div>
        <button onClick={previousSlide}>Previous</button>
        <img src={props.images[currentIndex]} alt="Slide" />
        <button onClick={nextSlide}>Next</button>
      </div>
    );
}
