import React, { useState } from "react";
import './Collapse.css'

export default function Collapse(props){
  const [open, setOpen] = useState(false);
  return (
    <div>
      <div className="title" onClick={() => setOpen(!open)}>
        <h2>{props.title}</h2>
      </div>
      {open ? (
        <div className="text">
          <p>{props.description}</p>
        </div>
      ) : null}
    </div>
  )
}
