import React from "react";
import { BrowserRouter as Route } from 'react-router-dom'
import Card from "./Card/Card";
import Full from "./Full/Full"
import Error from "../../pages/Error/Error";
import cards from '../../json/housing.json';
import Home from "../../pages/Home/Home";
import Housing from "../../pages/Housing/Housing";
import './Logement.css'

export default function Logement(){

    const cardList = cards.map(card => (
        <Card key={card.id} id={card.id} title={card.title} description={card.description} cover={card.cover} />
    ))


    if(<Route exact path="/" element={<Home/>}/>){
        return (
        <div className="cardList">
            {cardList}
        </div>
        )
    }

    if (<Route exact path="/housing/:id" element={<Housing/>} /> ){
        return(
            <Full />
        )
    }
}

