import React from "react"
import Header from "../../components/Header/Header"
import { useParams } from "react-router-dom";
import logements from '../../json/housing.json'
import Carousel from "../../components/Carousel/Carousel";
import Collapse from '../../components/Collapse/Collapse'
import './Housing.css'
import Tag from "../../components/Tag/Tag";
import Footer from "../../components/Footer/Footer";
import Error from '../Error/Error'

export default function Housing() {
    const { id } = useParams();
    const housings = logements.find((housing) => housing.id === id);

    if(housings === undefined){
        return (
            <Error />
        )
    }

    const TagList = housings.tags.map(tag => (
        <Tag key={tag} tags={tag} />
      ))
      const EquipmentList = housings.equipments.map(equipment => (
        <div key={equipment}>{equipment}</div>
      ))

        return (
            <div>
                <Header />
                <section className="bodyHousing" >
                    <Carousel images={housings.pictures}/>
                    <div className="housing">
                        <div className="housingTitle">
                            <h1>{housings.title}</h1>
                            <p>{housings.location}</p>
                        </div>
                        <div className="housingHost">
                            <p>{housings.host.name}</p>
                            <img src={housings.host.picture} alt={housings.host.name}/>
                        </div>
                    </div>
                    <div>
                        <div className="tagList">{TagList}</div>
                    </div>
                </section>
                <div className="description">
                    <div className="housingDescription"><Collapse title={'Description'} description={housings.description}/></div>
                    <div className='housingDescription'><Collapse title={'Équipements'} description={EquipmentList}/></div>  
                </div>
                <Footer />
            </div>
        )
}
