import React from "react";
import './Carouselle.css'
import { useState } from "react";

export default function Carouselle(props){
    const [currentIndex, setCurrentIndex] = useState();

    const previousSlide = () => {
      const newIndex = currentIndex - 1;
      setCurrentIndex(newIndex < 0 ? props.pictures.length - 1 : newIndex);
    };

    const nextSlide = () => {
      const newIndex = currentIndex + 1;
      setCurrentIndex(newIndex === props.pictures.length ? 0 : newIndex);
    };

    return (
      <div>
        <button onClick={previousSlide}>Previous</button>
        <img src={props.pictures[currentIndex]} alt="Slide" />
        <button onClick={nextSlide}>Next</button>
      </div>
    );
}
