import React from "react";
import { BrowserRouter as Route } from 'react-router-dom'
import Card from "./Card/Card";
import Full from "./Full/Full"
import cards from '../../json/housing.json'
import './Logement.css'

export default function Logement(){

    const cardList = cards.map(card => (
        <Card key={card.id} id={card.id} title={card.title} description={card.description} cover={card.cover} />
    ))


        if(<Route path='/' />){
            return (
            <div>
                {cardList}
            </div>
            )
        } else if (<Route path='/housing' />){
            return(
                <Full />
            )

    }
}

