import React from 'react';
import './Home.css';
import Header from '../../components/Header/Header';
import Footer from '../../components/Footer/Footer';
import bannière from '../../assets/IMG.png'
import Card from '../../components/Card/Card';
import Banner from '../../components/Banner/Banner'
import cards from '../../json/housing.json'

function Home() {

  const cardList = cards.map((card, index) => (
    <Card key={index} title={card.title} description={card.description} cover={card.cover} />
  ))


  return (
    <div className="App">
      <Header />
      <section className='body'>
        <div className='bannière'>
          <Banner src={bannière} alt='bannière'/>
          <h1 className='bannièreText'>Chez vous, partout et ailleurs</h1>
        </div>
        <div className='housing'>
        {cardList}
        </div>
      </section>

      <Footer />
    </div>
  );
}

export default Home;
